import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addPost, updatePaper } from "../../actions/post";
import React, { useState, useEffect } from "react";
import { TextField, Button, Typography, Paper } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import FileBase from "react-file-base64";

import useStyles from "./styles";

const PostForm = ({ addPost, currentId, setCurrentId }) => {
  const [text, setText] = useState("");
  const [postData, setPostData] = useState({
    creator: "",
    name: "",
    mail: "",
    phone: "",
    message: "",
    tags: "",
    selectedFile: "",
  });
  const clear = () => {
    setCurrentId(0);
    setPostData({
      creator: "",
      name: "",
      mail: "",
      phone: "",
      message: "",
      tags: "",
      selectedFile: "",
    });
  };
  const post = useSelector((state) =>
    currentId ? state.posts.find((message) => message._id === currentId) : null
  );
  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);
  const handleOnSubmit = (e) => {
    e.preventDefault();
    addPost({ text });
    setText("");
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (currentId === 0) {
      dispatch(addPost(postData));
      clear();
    } else {
      dispatch(updatePaper(currentId, postData));
      clear();
    }
  };

  return (
    <div>
      <div className="post-form">
        <div className="post-form-header bg-primary">
          <h3>Say Something...</h3>
        </div>

        <form className="form my-1" onSubmit={handleOnSubmit}>
          <textarea
            name="text"
            cols="30"
            rows="5"
            placeholder="Create a Post"
            value={text}
            onChange={(e) => setText(e.target.value)}
            required
          />

          <input type="submit" value="Submit" className="btn btn-dark my-1" />
        </form>
      </div>
      <Paper className={classes.paper}>
        <form
          autoComplete="off"
          noValidate
          className={`${classes.root} ${classes.form}`}
          onSubmit={handleSubmit}
        >
          <Typography variant="h6">
            {currentId ? `Editing "${post.name}"` : "Creating a name"}
          </Typography>
          <TextField
            name="creator"
            variant="outlined"
            label="Creator"
            fullWidth
            value={postData.creator}
            onChange={(e) =>
              setPostData({ ...postData, creator: e.target.value })
            }
          />
          <TextField
            name="name"
            variant="outlined"
            label="Name"
            fullWidth
            value={postData.name}
            onChange={(e) => setPostData({ ...postData, name: e.target.value })}
          />
          <TextField
            name="mail"
            variant="outlined"
            label="Mail"
            fullWidth
            value={postData.mail}
            onChange={(e) => setPostData({ ...postData, mail: e.target.value })}
          />
          <TextField
            name="phone"
            variant="outlined"
            label="Phone"
            fullWidth
            value={postData.phone}
            onChange={(e) =>
              setPostData({ ...postData, phone: e.target.value })
            }
          />
          <TextField
            name="message"
            variant="outlined"
            label="Message"
            fullWidth
            multiline
            rows={4}
            value={postData.message}
            onChange={(e) =>
              setPostData({ ...postData, message: e.target.value })
            }
          />
          <TextField
            name="tags"
            variant="outlined"
            label="Tags (Urgent)"
            fullWidth
            value={postData.tags}
            onChange={(e) =>
              setPostData({ ...postData, tags: e.target.value.split(",") })
            }
          />
          <div className={classes.fileInput}>
            <FileBase
              type="file"
              multiple={false}
              onDone={({ base64 }) =>
                setPostData({ ...postData, selectedFile: base64 })
              }
            />
          </div>
          <Button
            className={classes.buttonSubmit}
            variant="contained"
            color="primary"
            size="large"
            type="submit"
            fullWidth
          >
            Submit
          </Button>
          <Button
            variant="contained"
            color="secondary"
            size="small"
            onClick={clear}
            fullWidth
          >
            Clear
          </Button>
        </form>
      </Paper>
    </div>
  );
};

PostForm.propTypes = {
  addPost: PropTypes.func.isRequired,
};

export default connect(null, { addPost })(PostForm);
