import React from "react";
import Joi from "@hapi/joi";
import { connect } from "react-redux";

import Input from "../../components/common/Input";
import Select from "../../components/common/Select";
import { addPatient } from "../../actions/patientsAction";
import { patientSchema } from "./schema";

class AddPatientForm extends React.Component {
  state = {
    data: {
      name: "",
      genre: "",
      mail: "",
      numberphone: "",
      description: "",
      image: null,
    },
    genres: [],
    errors: {},
  };

  handleChange = ({ currentTarget: input }) => {
    const data = { ...this.state.data };
    data[input.name] = input.value;
    this.setState({ data });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    //TODO: Validate property
    const { error } = Joi.validate(this.state, patientSchema);
    this.setState({ errors: error });
    if (!error) this.props.addMovie(this.state.data);
  };

  uploadImage = (e) => {
    if (e.target.files[0]) {
      const data = { ...this.state.data };
      data.image = e.target.files[0];
      this.setState({ data });
    }
  };

  render() {
    const { errors, data } = this.state;
    const { name, genre, numberphone, mail } = data;
    const { genres } = this.props;

    return (
      <div className="background-container pt-5">
        <div className="container">
          <h1 className="header">Add a new Patient</h1>

          <form onSubmit={this.handleSubmit} encType="multipart/form-data">
            <Input
              name="name"
              value={name}
              label="Name"
              onChange={this.handleChange}
              placeholder="Enter the name..."
              error={errors["name"]}
              iconClass="fas fa-film"
              autoFocus
            />

            <Select
              name="genre"
              label="Genre"
              onChange={this.handleChange}
              value={genre}
              error={errors["genre"]}
              options={genres}
              iconClass="fas fa-address-card"
            />
            <Input
              name="email"
              value={mail}
              label="Email"
              onChange={this.handleChange}
              placeholder="Enter the email..."
              error={errors["mail"]}
              iconClass="fas fa-film"
              autoFocus
            />

            <Input
              name="numberphone"
              label="Number Phone"
              onChange={this.handleChange}
              placeholder="Enter  your numbers ..."
              error={errors["numberphone"]}
              iconClass="fas fa-hashtag"
              value={numberphone}
              type="number"
            />

            <Input
              name="image"
              label="Cover Image"
              onChange={this.uploadImage}
              error={errors["coverImage"]}
              iconClass="fas fa-file-image"
              accept="image/*"
              type="file"
            />

            <Input
              name="description"
              label="Description"
              placeholder="Enter description about this movie..."
              iconClass="fas fa-info"
              error={errors["description"]}
              type="textarea"
            />
          </form>
        </div>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    addPatient: (patient) => dispatch(addPatient(patient)),
  };
};

const mapStateToProps = (state) => {
  return {
    genres: state.genre.genres,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AddPatientForm);
