import { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import React from "react"
import { addPost } from '../../actions/post';



const PostForm = ({ addPost }) => {
  const [text, setText] = useState('');
  const [selectedFile, uploadimg] = useState([])
  console.log("Image FIles",selectedFile);



  const handleOnSubmit = (e) => {
    e.preventDefault();
    addPost({ text });
    setText('');
  };

  const imgFilehandler = (e) => {
    if (e.target.files.length !== 0) {
      uploadimg(selectedFile => [...selectedFile, URL.createObjectURL(e.target.files[0])])
    }
  }

  return (
    <div className='post-form'>
      <div className='post-form-header bg-primary'>
        <h3>Say Something...</h3>
      </div>

      <form className='form my-1' onSubmit={handleOnSubmit}>
        <textarea
          name='text'
          cols='30'
          rows='5'
          placeholder='Create a Post'
          value={text}
          onChange={(e) => setText(e.target.value)}
          required
        />
        <input type='submit' value='Submit' className='btn btn-dark my-1' />
        <center>
          <h2>Upload</h2>
          <input type="file" onChange={imgFilehandler} />
          <hr />
          <h2>Preview</h2>
          {selectedFile.map((elem) => {
            return <>
              <span key={elem}>
                <img src={elem} height="100" width="100" alt="med1" />
              </span>
            </>
          })}
        </center>
      </form>
    </div>
  );
};

PostForm.propTypes = {
  addPost: PropTypes.func.isRequired,
};

export default connect(null, { addPost })(PostForm);
