import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import _ from "lodash";

import { search, categorize } from "../../utils";
import { PatientsTable, Pagination } from "../../components";
import { Input, Loading, ListGroup } from "../../components/common";

import { getPatients } from "../../actions/patientsAction";
import { getZheimers } from "../../actions/zheimerAction";

class Patients extends Component {
  state = {
    genres: [],
    pageSize: 12,
    currentPage: 1,
    currentZheimer: "All",
    searchFilter: "",
  };

  componentDidMount() {
    this.props.getPatients();
    this.props.getZheimers();
  }

  handleChange = (name, value) => {
    this.setState({ [name]: value, currentPage: 1 });
  };

  onPageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const { currentZheimer, currentPage, searchFilter, pageSize } = this.state;

    const { patients, zheimers, loggedIn } = this.props;

    if (_.isEmpty(patients)) {
      return (
        <div className="background-container pt-5">
          <Loading />
        </div>
      );
    }

    let filteredPatients = [];

    /* Checking for searched item if nothing searched it will just set it to allMovies*/
    filteredPatients = search(patients, searchFilter, "title");
    filteredPatients = categorize(filteredPatients, currentZheimer);
    //filteredPatients = filterRating(filteredPatients);

    return (
      <div className="background-container">
        <div className="mx-5 py-5">
          <div className="row">
            <div className="col-lg-2 col-sm-12">
              <h4 className="text-muted text-left p-1">Filters</h4>
              <ListGroup
                active={currentZheimer}
                onChange={(val) => this.handleChange("currentZheimer", val)}
                options={zheimers}
              />

              {loggedIn && (
                <Link
                  to="/patients/new"
                  className="btn btn-primary btn-block my-2 text-white"
                >
                  Add Patient
                </Link>
              )}
            </div>

            <div className="col-lg-10 col-sm-12">
              <Input
                onChange={(event) =>
                  this.handleChange("searchFilter", event.target.value)
                }
                label="Search Patient"
                iconClass="fas fa-search"
                placeholder="Search..."
              />
              <p className="text-left text-muted">
                {!!filteredPatients.length ? `${filteredPatients.length}` : "0"}
                patients found.
              </p>

              {!!filteredPatients ? (
                <PatientsTable
                  pageSize={pageSize}
                  currentPage={currentPage}
                  patients={filteredPatients}
                />
              ) : (
                <h1 className="text-white">No Patients</h1>
              )}
              <br />

              <Pagination
                itemsCount={filteredPatients.length}
                pageSize={pageSize}
                onPageChange={this.onPageChange}
                currentPage={currentPage}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    patients: state.patient.patients,
    zheimers: state.zheimer.zheimers,
    loggedIn: state.auth.loggedIn,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getPatients: () => dispatch(getPatients()),
    getZheimers: () => dispatch(getZheimers()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Patients);
