import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Container, Grow, Grid } from "@material-ui/core";
import PostsPaper from "../posts/PostsPaper";
import Form from "../posts/PaperForm";
import { getPostsPaper } from "../../actions/post";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { logout } from "../../actions/auth";
const Paper = ({ auth: { isAuthenticated, loading } }) => {
  const [currentId, setCurrentId] = useState(0);
  const dispatch = useDispatch();
  //const classes = useStyles();

  useEffect(() => {
    dispatch(getPostsPaper());
  }, [currentId, dispatch]);
  return (
    <Grow in>
      <Container>
        <Grid
          container
          justify="space-between"
          alignItems="stretch"
          spacing={3}
        >
          <Grid item xs={12} sm={7}>
            <PostsPaper setCurrentId={setCurrentId} />
          </Grid>
          <Grid item xs={12} sm={4}>
            <Form currentId={currentId} setCurrentId={setCurrentId} />
          </Grid>
        </Grid>
      </Container>
    </Grow>
  );
};

Paper.propTypes = {
  //logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({ auth: state.auth });

export default connect(mapStateToProps)(Paper);
